package com.example.githubuserapp.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.example.githubuserapp.data_model.GithubUserDataimport com.example.githubuserapp.databinding.LayoutItemBindingclass GithubUserAdapter(private var listUserGithub : ArrayList<GithubUserData>) : RecyclerView.Adapter<GithubUserAdapter.GithubViewHolder>() {    class GithubViewHolder (val binding: LayoutItemBinding) : RecyclerView.ViewHolder(binding.root)    private lateinit var onItemClikCallBack : OnItemClickCallback    fun setOnItemClickCallback(onItemClickCallback: OnItemClickCallback){        this.onItemClikCallBack = onItemClickCallback    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): GithubViewHolder {        return GithubViewHolder(LayoutItemBinding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: GithubViewHolder, position: Int) {           holder.binding.apply {               holder.itemView.setOnClickListener {                   onItemClikCallBack.onItemClicked(listUserGithub[position])               }               tvName.text = listUserGithub[position].name               tvUsername.text = listUserGithub[position].username               tvCompany.text = listUserGithub[position].company               Glide.with(holder.itemView.context)                   .load(listUserGithub[position].avatar)                   .circleCrop()                   .into(holder.binding.avatar)           }    }    override fun getItemCount(): Int = listUserGithub.size    interface OnItemClickCallback{        fun onItemClicked(data : GithubUserData)    }}